import './App.css';
import Header from './MyComponents/Header';
import {Todos} from "./MyComponents/Todos";
import {Footer} from './MyComponents/Footer';

import React, {useState} from 'react'

function App() {
  const onDelete = (todo) => {
    setTodos(todos.filter( (e) => {
      return e !==todo;
    }))
  }

  const [todos, setTodos] = useState([
    {
      sno: 1,
      title: "Need to go to market",
      description: "You need to go to the market to get the iftar goods"
    },
    {
      sno: 2,
      title: "Need to finish react tutorial",
      description: "It's been 2 days you have started the react tutorial. Please finish it today"
    },
    {
      sno: 3,
      title: "It's time for namaz",
      description: "It's 08:30PM. It's namaz time"
    }
  ])
  return (
    <>
      <Header title="MyTodosList" searchBar={false}/>
      <Todos todos={todos} onDelete={onDelete}/>
      <Footer/>
    </>
  );
}

export default App;
